import React, { Component } from 'react';
import './App.css';
import Particles from 'react-particles-js';

import NavigationBar from './components/navigation/navbar.js';
import Logo from './components/logo/logo.js';
import ImgForm from './components/body/imageform.js';
import Rank from './components/rank/rank.js';
import {connect} from 'react-redux';
import {ActionText} from './redux/actions/action';
import Clarifai from 'clarifai';



const particls={
  "particles": {
    "number": {
      "value": 30,
      "density": {
        "enable": true,
        "value_area": 552.4033491425909
      }
    },
    "color": {
      "value": "#ffffff"
    },
    "shape": {
      "type": "circle",
      "stroke": {
        "width": 0,
        "color": "#000000"
      },
      "polygon": {
        "nb_sides": 5
      },
      "image": {
        "src": "img/github.svg",
        "width": 100,
        "height": 100
      }
    },
    "opacity": {
      "value": 0.5,
      "random": false,
      "anim": {
        "enable": false,
        "speed": 1,
        "opacity_min": 0.1,
        "sync": false
      }
    },
    "size": {
      "value": 3,
      "random": true,
      "anim": {
        "enable": false,
        "speed": 40,
        "size_min": 0.1,
        "sync": false
      }
    },
    "line_linked": {
      "enable": true,
      "distance": 150,
      "color": "#ffffff",
      "opacity": 0.4,
      "width": 1
    },
    "move": {
      "enable": true,
      "speed": 6,
      "direction": "none",
      "random": false,
      "straight": false,
      "out_mode": "out",
      "bounce": false,
      "attract": {
        "enable": false,
        "rotateX": 600,
        "rotateY": 1200
      }
    }
  },
  "interactivity": {
    "detect_on": "canvas",
    "events": {
      "onhover": {
        "enable": true,
        "mode": "repulse"
      },
      "onclick": {
        "enable": true,
        "mode": "repulse"
      },
      "resize": true
    },
    "modes": {
      "grab": {
        "distance": 400,
        "line_linked": {
          "opacity": 1
        }
      },
      "bubble": {
        "distance": 400,
        "size": 40,
        "duration": 2,
        "opacity": 8,
        "speed": 3
      },
      "repulse": {
        "distance": 200,
        "duration": 0.4
      },
      "push": {
        "particles_nb": 4
      },
      "remove": {
        "particles_nb": 2
      }
    }
  },
  "retina_detect": true
}
const app = new Clarifai.App({
 apiKey: '06b9b66bef864791839c7e5cdeea0e6f'
});

class App extends Component {
//   constructor(props){
//   super();
//   this.buttonClick=this.buttonClick;
// }

  buttonClick(){
    app.models.predict("a403429f2ddf4b49b307e318f00e528b", "https://samples.clarifai.com/face-det.jpg").then(
    function(response) {
      // do something with response
      console.log(response.outputs);
    },
    function(err) {
      // there was an error
    }
  );
  }

  render() {

    return (
      <div className="App">
        <Particles params={particls} className="particle" />
        <NavigationBar/>
        <Logo/>
        <Rank/>
        <ImgForm url={this.props.onTextChange} buttonClick={this.buttonClick}/>
      </div>
    );
  }
}


const mapStateToProps=(state)=>({
  urlChange:state
})

const mapActionToProps=(dispatch)=>({
  onTextChange:(event)=>dispatch(ActionText(event.target.value))
})

export default connect(mapStateToProps,mapActionToProps)(App);
