// import React from 'react';

export const URL_LOAD='URL_LOAD';
export const  IMAGE_BOX='IMAGE_BOX';
export const  ROUTER_TEXT='ROUTER_TEXT';

export const ActionText=(text)=>{
  return ({
    type:URL_LOAD,
    payload:text
  });
}
export const ImageBox=(obj)=>{
  return ({
    type:IMAGE_BOX,
    payload:obj,
  });
}
export const RouterAction=(text)=>{
  return ({
    type:IMAGE_BOX,
    payload:{value:text,
            isEnable:false},
  });
}
